import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [contacts, setContacts] = useState([]);
  const [summary, setSummary] = useState({ found: 0, errors: 0 });
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      alert('Please select a file first.');
      return;
    }

    setLoading(true);
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:5000/process', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setContacts(response.data.contacts);
      setSummary(response.data.summary);
    } catch (error) {
      console.error('Error processing file:', error);
      alert('An error occurred while processing the file.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-8">
          <div className="card">
            <div className="card-header text-center">
              <h1>Personalized WhatsApp Messaging</h1>
            </div>
            <div className="card-body">
              <p className="card-text">
                Upload an Excel file with two columns: 'name' (Column A) and 'telephone' (Column B) to generate personalized WhatsApp "Click to Chat" links.
              </p>
              <form onSubmit={handleSubmit}>
                <div className="mb-3">
                  <input className="form-control" type="file" accept=".xlsx" onChange={handleFileChange} />
                </div>
                <button type="submit" className="btn btn-primary w-100" disabled={loading}>
                  {loading ? 'Processing...' : 'Generate Links'}
                </button>
              </form>

              {summary.found > 0 && (
                <div className="alert alert-info mt-4">
                  {`Processing complete. Found: ${summary.found} contacts, Errors: ${summary.errors}`}
                </div>
              )}

              {contacts.length > 0 && (
                <div className="mt-4">
                  <h2>Generated Links</h2>
                  <p>Click each button to open WhatsApp and send the message manually.</p>
                  <ul className="list-group">
                    {contacts.map((contact, index) => (
                      <li key={index} className="list-group-item d-flex justify-content-between align-items-center">
                        {contact.name} ({contact.telephone})
                        <a
                          href={contact.link}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="btn btn-success"
                        >
                          Send Message
                        </a>
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
